mapper.select:
SELECT COUNT(*) AS CNT
FROM ACADEMIC_CHG
WHERE STU_NUM = #{stuNum}
AND AC_LIST = #{acList}
AND AC_CHECK = 0

SELECT NVL(STU_STATUS, 0) AS STU_STATUS
FROM STUDENT
WHERE STU_NUM = #{stuNum}

SELECT	COUNT(DM_NUM) AS CNT
FROM	DOUBLE_MAJOR
WHERE	STU_NUM = #{stuNum}
AND		DM_DIVISION = #{dmDivision}
AND		NVL(DM_CHECK,0) = #{dmCheck}

mapper.insert:
INSERT INTO ACADEMIC_CHG(
						  AC_NUM, STU_NUM, AC_REASON, AC_LIST, AC_SDT
						, AC_EDT, AC_CHECK, AC_COMRE
)VALUES(
		 SEQ_ACADEMIC_CHANGE.NEXTVAL, #{stuNum}, #{acReason}, 2, #{acSdt}
	   , NULL, 0, NULL)

mapper.select:
SELECT	DEPT_NUM
      , (
          	SELECT COUNT(STU_NUM) AS CNT_AL
            FROM STUDENT
            WHERE DEPT_NUM = #{deptNum}
            AND (STU_STATUS = 0
                 OR STU_STATUS = 1)
          )AS CNT_AL
       , (
            SELECT DEPT_NUMBER
            FROM DEPARTMENT
            WHERE DEPT_NUM = #{deptNum}
         )AS DEPT_NUMBER
FROM	DEPARTMENT
WHERE	DEPT_NUM = #{deptNum}

	SELECT (
            (
                SELECT A.DEPT_FEES
                FROM DEPARTMENT A
                WHERE A.DEPT_NUM = #{preDeptNum}
            )
         - (
                SELECT B.DEPT_FEES
                FROM DEPARTMENT B
                WHERE B.DEPT_NUM = #{tranDeptNum}
            )
        ) AS ADDFEE
	FROM DUAL

SELECT	COUNT(NVL(TD_NUM, 0)) AS CNT_TR
FROM	TRANSFER_DP
WHERE	STU_NUM = #{stuNum}
AND		TD_CHECK = #{tdCheck}

SELECT  TD_NUM, DEPT_NUM, STU_NUM, TD_DT, TD_CHECK, NVL(TD_COMRE,'nodata') TD_COMRE, TD_OLD
FROM    TRANSFER_DP
WHERE   TD_NUM = #{tdNum}

SELECT NUM, CHG_CHECK, STU_NUM, DEPT_NM, STU_NM_KOR , CONT, REASON FROM( SELECT AC_NUM AS NUM , REPLACE(A.AC_CHECK, '0', '대기')AS CHG_CHECK , A.STU_NUM , C.DEPT_NM , B.STU_NM_KOR , REPLACE(REPLACE(REPLACE(REPLACE(A.AC_LIST, '0', '복학'), '1', '휴학'), '2', '자퇴'), '3', '조기졸업') AS CONT , NVL(A.AC_REASON, ' ') AS REASON FROM ACADEMIC_CHG A LEFT OUTER JOIN STUDENT B ON(B.STU_NUM = A.STU_NUM) LEFT OUTER JOIN DEPARTMENT C ON(C.DEPT_NUM = B.DEPT_NUM) WHERE A.AC_CHECK = 0
UNION ALL
SELECT GP_NUM AS NUM , REPLACE(A.GP_CHECK, '0', '대기')AS CHG_CHECK , A.STU_NUM , C.DEPT_NM , B.STU_NM_KOR , '졸업유예' AS CONT , ' ' AS REASON FROM GRADUATION_POST A LEFT OUTER JOIN STUDENT B ON(B.STU_NUM = A.STU_NUM) LEFT OUTER JOIN DEPARTMENT C ON(C.DEPT_NUM = B.DEPT_NUM) WHERE GP_CHECK = 0 ORDER BY NUM ASC ) WHERE NUM = #{num} AND CONT = #{cont}

mapper.update:
UPDATE  STUDENT
SET     STU_STATUS = #{stuStatus}
WHERE   STU_NUM = #{stuNum}

UPDATE  ACADEMIC_CHG
SET     AC_CHECK = 1
WHERE   AC_NUM = #{acNum}

UPDATE  GRADUATION_POST
SET     GP_CHECK = 1
WHERE   GP_NUM = #{gpNum}

UPDATE  GRADUATION_POST
SET     GP_CHECK = 2
      , GP_COMRE = #{gpComre}
WHERE   GP_NUM = #{gpNum}

UPDATE  ACADEMIC_CHG
SET     AC_CHECK = 2
      , AC_COMRE = #{acComre}
WHERE   AC_NUM = #{acNum}

UPDATE  STUDENT
SET     STU_DMAJOR = #{stuDmajor}
WHERE   STU_NUM = #{stuNum}

UPDATE  DOUBLE_MAJOR
SET     DM_CHECK = 2
      , DM_COMRE = #{dmComre}
      , DM_CDT = SYSDATE
WHERE   DM_NUM = #{dmNum}

UPDATE  TRANSFER_DP
SET     TD_CHECK = 1
WHERE   TD_NUM = #{tdNum}

UPDATE  STUDENT
SET     DEPT_NUM = #{deptNum}
WHERE   STU_NUM = #{stuNum}

UPDATE  TRANSFER_DP
SET     TD_CHECK = 2
      , TD_COMRE = #{tdComre}
WHERE   TD_NUM = #{tdNum}
