mapper.select:
SELECT A.SBJ_NUM, A.DEPT_NUM, A.TEA_NUM, A.SBJ_TITLE, A.SBJ_GRADE, A.SBJ_RECODE, A.SBJ_PERSONNEL, A.SBJ_YN, A.SBJ_COMRE, A.SBJ_OC, A.SBJ_RS, B.TEA_NM_KOR, C.DEPT_NM, (SELECT COUNT(*) FROM LECTURE_TM D WHERE A.SBJ_NUM = D.SBJ_NUM) COUNT FROM SUBJECT A, TEACHER B, DEPARTMENT C WHERE A.TEA_NUM = B.TEA_NUM AND A.DEPT_NUM = C.DEPT_NUM
AND A.TEA_NUM = #{teaNum}
ORDER BY 1 DESC

mapper.update:
UPDATE SUBJECT_PLAN 
SET SP_MIDDLERATIO = #{spMiddleratio}, SP_FINALRATIO = #{spFinalratio}, SP_HWRATIO = #{spHwratio}, 
	SP_ATTRATIO = #{spAttratio}, SP_ETCRATIO = #{spEtcratio}, SP_ONLINEYN = #{sbjYn}, SP_TEXTBOOK = #{spTextbook}
WHERE SP_NUM = #{spNum}
  AND SBJ_NUM = #{sbjNum}

MERGE INTO SUBJECT_INDEX A USING (
SELECT #{siVO.siNum} AS SI_NUM, #{siVO.spNum} AS SP_NUM, #{siVO.sbjNum} AS SBJ_NUM, #{siVO.siTarget} AS SI_TARGET FROM DUAL
) B ON (A.SP_NUM = B.SP_NUM AND A.SI_NUM=B.SI_NUM) WHEN MATCHED THEN UPDATE SET SI_TARGET = B.SI_TARGET WHEN NOT MATCHED THEN INSERT (SI_NUM, SP_NUM, SBJ_NUM, SI_TARGET) VALUES(B.SI_NUM, B.SP_NUM, B.SBJ_NUM, B.SI_TARGET)

mapper.select:
SELECT ROOM_CODE, BUILDING_CODE, ROOM_FLR, ROOM_CAPACITY, ROOM_LECT, ROOM_PHOTO
FROM ROOM
WHERE BUILDING_CODE = #{buildingCOde}

mapper.update:
MERGE INTO ROOM_SCHEDULE A USING (
SELECT #{ltVO.roomCode} AS ROOM_CODE, #{ltVO.timeTcode} AS TIME_TCODE FROM DUAL
) B ON (A.ROOM_CODE = B.ROOM_CODE AND A.TIME_TCODE=B.TIME_TCODE) WHEN MATCHED THEN UPDATE SET RS_ISEMPTY = 1 WHEN NOT MATCHED THEN INSERT (ROOM_CODE, TIME_TCODE, RS_ISEMPTY) VALUES(B.ROOM_CODE, B.TIME_TCODE, 1)

INSERT INTO LECTURE_TM (LT_NUM, SBJ_NUM, ROOM_CODE, TIME_TCODE) SELECT SEQ_LECTURE_TIME.NEXTVAL AS LT_NUM , T.* FROM (
SELECT #{ltVO.sbjNum} as SBJ_NUM, #{ltVO.roomCode} as ROOM_CODE, #{ltVO.timeTcode} as TIME_TCODE FROM DUAL
) T

UPDATE SUBJECT 
SET SBJ_YN = 2, SBJ_COMRE = #{sbjComre}
WHERE SBJ_NUM = #{sbjNum}
