mapper.select:
SELECT COUNT(*)
FROM VOTERS
WHERE VOTE_NUM = #{voteNum} AND VOTERS_STU = #{votersStu}

SELECT VOTE_NUM, VOTE_TITLE, TO_CHAR(VOTE_ENDDT,'YYYY-MM-DD') AS voteDate, VOTE_ADMIN, S_NUM, PERIOD
FROM VOTE
WHERE VOTE_NUM = #{voteNum}

mapper.update:
UPDATE VOTERS
SET
VOTE_ANS = #{voteAns}
WHERE VOTE_NUM = #{voteNum} AND VOTERS_STU = #{votersStu}
